let cartItemsList=document.querySelector(".cart-dropdown-menu");cartItemsList.onclick=e=>e.stopPropagation(),function(){let e=document.getElementsByClassName("product"),t=document.getElementsByClassName("love-icon");for(let o=0;e.length,o<t.length;o+=1)e[o].ondblclick=function(){this.classList.contains("loved")?(this.classList.remove("loved"),t[o].style.color="#000",t[o].setAttribute("class","far fa-heart love-icon")):(this.classList.add("loved"),t[o].style.color="#f60000",t[o].setAttribute("class","fas fa-heart love-icon"))}}();let searchIcon=document.querySelector(".search-icon"),searchWindow=document.querySelector(".search-window"),searchField=document.querySelector(".search-field"),closeButton=document.querySelector(".close-button");searchIcon.onclick=function(){(searchWindow.style.display="none")?searchWindow.style.display="block":searchWindow.style.display="none"},closeButton.onclick=function(){searchWindow.style.display="none"};